import { ExternalReference } from '../../common/types';
/**
 * Interface for text embedding functionality.
 * Defines the core methods required for any text embedder implementation.
 */
export interface TextEmbedderInterface {
    /**
     * Returns the external reference to the native embedder implementation.
     *
     * @returns External reference object
     */
    getExternal(): ExternalReference;
    /**
     * Cleans up resources associated with this text embedder.
     */
    destroy(): void;
}
